document.addEventListener("DOMContentLoaded",function(){const o=document.getElementById("form"),d=document.getElementById("resultado");o.addEventListener("submit",function(e){e.preventDefault();var t,e=parseInt(document.getElementById("numero1").value),n=parseInt(document.getElementById("numero2").value);isNaN(e)||isNaN(n)?d.textContent="Por favor, digite dois números válidos.":(t=e+n,d.textContent=`A soma de ${e} e ${n} é igual a ${t}.`,o.reset())})});